	/**
	 * @param string
	 * @return
	 */
	final private static String getTokenedPropertyName(int intPpropertyIndex) {

		switch (intPpropertyIndex) {
			case Language.intS_QUESTION_OVERWRITE_EXISTING_FILE_TOKEN_1:
			case Language.intS_QUESTION_OVERWRITE_EXISTING_FILE_TOKEN_2:
			case Language.intS_MESSAGE_FILE_TOKEN_UNREACHABLE_OR_UNFOUNDABLE:
			case Language.intS_MESSAGE_CONFIGURE_YOUR_NAVIGATOR_TO_VISIT_THE_PAGE_TOKEN_1:
			case Language.intS_MESSAGE_CONFIGURE_YOUR_NAVIGATOR_TO_VISIT_THE_PAGE_TOKEN_2:
			case Language.intS_MESSAGE_SOFTWARE_TOKEN_IMPROVED_VERSION_OF_PREVIOUS_RELEASES:
				for (final String objLtoken : Language.strS_TOKEN_PATTERN_A) {
					if (Language.strS_PROPERTY_NAME_A[intPpropertyIndex].contains(objLtoken)) {
						return Language.strS_PROPERTY_NAME_A[intPpropertyIndex].replace(objLtoken,
																						Language.strS_TOKEN_A[Language.bytS_TOKEN_FILE_NAME]);
					}
				}
				//$FALL-THROUGH$
			default:
				return Language.strS_PROPERTY_NAME_A[intPpropertyIndex];
		}
	}

