	/**
	 * Method description
	 * 
	 * @see
	 * @param objPbufferedReader
	 */
	final private void doLoadLanguageFile(BufferedReader objPbufferedReader) {

		final byte bytL_PARAMETER_NAME_STATE = 0;
		final byte bytL_EQUALS_SYMBOL_STATE = 1;
		final boolean[] bolLparameterSetA = new boolean[Language.intS_LANGUAGE_PROPERTIES_NUMBER];
		Arrays.fill(bolLparameterSetA, false);
		final StringBuilder objLfileLineStringBuilder = new StringBuilder(32);
		final StringBuilder objLparameterNameStringBuilder = new StringBuilder(16);
		byte bytLsection = Language.bytS_SECTION_ROOT;
		boolean bolLsomethingRead = false;

		while (true) {
			try {
				objLfileLineStringBuilder.setLength(0);
				String strLread = Strings.strS_EMPTY;
				while (strLread.length() == 0) {
					strLread = Strings.untabTrim(objPbufferedReader.readLine());
				}
				objLfileLineStringBuilder.append(strLread);
			} catch (final Throwable objPthrowable) {
				break;
			}
			final char[] chrLfileLineStringA = objLfileLineStringBuilder.toString().toCharArray();

			// Pass comments :
			if (chrLfileLineStringA[0] == ';') {
				continue;
			}
			bolLsomethingRead = true;

			// Get the section name :
			if (chrLfileLineStringA[0] == '[') {
				if (chrLfileLineStringA.length >= 2 && chrLfileLineStringA[chrLfileLineStringA.length - 1] == ']') {
					final String strLsection = Strings.unspace(new String(chrLfileLineStringA, 1, chrLfileLineStringA.length - 2));
					for (byte bytLsectionIndex = 0; bytLsectionIndex < Language.bytS_SECTIONS_NUMBER; ++bytLsectionIndex) {
						if (strLsection.equalsIgnoreCase(Language.strS_SECTION_A[bytLsectionIndex])) {
							bytLsection = bytLsectionIndex;
							break;
						}
					}
					if (bytLsection == Language.bytS_SECTION_ROOT) {
						Tools.err("Unknown language file section '", strLsection, "'");
					}
				}
				continue;
			}

			// Get the parameter name :
			byte bytLstate = bytL_PARAMETER_NAME_STATE;
			int intLcharIndex = 0;
			objLparameterNameStringBuilder.setLength(0);
			for (; bytLstate != bytL_EQUALS_SYMBOL_STATE && intLcharIndex < chrLfileLineStringA.length; ++intLcharIndex) {
				final char chrLindexed = chrLfileLineStringA[intLcharIndex];
				switch (chrLindexed) {
					case ' ':
						break;
					case '=':
						bytLstate = bytL_EQUALS_SYMBOL_STATE;
						break;
					default:
						objLparameterNameStringBuilder.append(chrLindexed);
				}
			}

			// Change the parameter value :
			if (bytLstate == bytL_EQUALS_SYMBOL_STATE) {
				final Integer objLpropertyIndexInteger =
															Language.objS_SECTION_PROPERTY_NAME_HASHMAP_A[bytLsection].get(objLparameterNameStringBuilder	.toString()
																																							.toLowerCase());
				if (objLpropertyIndexInteger != null) {
					final int intLpropertyIndex = objLpropertyIndexInteger.intValue();
					this.strGpropertyA[intLpropertyIndex] = Strings.untabTrim(objLfileLineStringBuilder.substring(intLcharIndex));
					bolLparameterSetA[intLpropertyIndex] = true;
					if (intLpropertyIndex == Language.intS_MESSAGE_SEPARATOR_LINE && !this.strGpropertyA[intLpropertyIndex].startsWith(";")) {
						this.strGpropertyA[intLpropertyIndex] = Strings.doConcat("; ", this.strGpropertyA[intLpropertyIndex]);
					}
				} else {
					Tools.err(	"Unknown property '",
								Strings.getFixedLengthString(	Strings.doConcat(objLparameterNameStringBuilder, '\''),
																Language.intS_LANGUAGE_PROPERTIES_NAMES_MAX_LENGTH,
																false,
																true,
																' '),
								" [section ",
								Strings.getFixedLengthString(	Language.strS_SECTION_A[bytLsection],
																Language.intS_LANGUAGE_SECTIONS_NAMES_MAX_LENGTH,
																false,
																true,
																' '),
								this.strGpropertyA[Language.intS_LANGUAGE_ISO_639_1_CODE] != null
																									? Strings.doConcat(	"] in language file 'data/",
																														this.strGpropertyA[Language.intS_LANGUAGE_ISO_639_1_CODE],
																														"/lang.ini'") : ']');
				}
			}
		}

		try {
			objPbufferedReader.close();
		} catch (final Throwable objPthrowable) {
			Tools.err("Error while closing language '", Language.strS_PROPERTY_NAME_A[Language.intS_LANGUAGE_ISO_639_1_CODE], "'file");
		}

		if (Constants.bolS_UNCLASS_ERRORS && bolLsomethingRead) {
			for (int intLpropertyIndex = 0; intLpropertyIndex < Language.intS_LANGUAGE_PROPERTIES_NUMBER; ++intLpropertyIndex) {
				if (intLpropertyIndex != Language.intS_LANGUAGE_ISO_639_1_CODE && !bolLparameterSetA[intLpropertyIndex]) {
					Tools.err(	"Missing language property default value '",
								Strings.getFixedLengthString(	Strings.doConcat(Language.strS_PROPERTY_NAME_A[intLpropertyIndex], '\''),
																Language.intS_LANGUAGE_PROPERTIES_NAMES_MAX_LENGTH,
																false,
																true,
																' '),
								" [section ",
								Strings.getFixedLengthString(	Language.strS_SECTION_A[Language.bytS_PROPERTY_SECTION_A[intLpropertyIndex]],
																Language.intS_LANGUAGE_SECTIONS_NAMES_MAX_LENGTH,
																false,
																true,
																' '),
								this.strGpropertyA[Language.intS_LANGUAGE_ISO_639_1_CODE] != null
																									? Strings.doConcat(	"] in language file './applet/data/",
																														this.strGpropertyA[Language.intS_LANGUAGE_ISO_639_1_CODE],
																														"/lang.ini'") : ']');
				}
			}
		}
	}

	/**
	 * Method description
	 * 
	 * @see
	 * @param objPbufferedReader
	 * @return
	 */
	final private String getHeaderString(BufferedReader objPbufferedReader) {
		final StringBuilder objLfileLineStringBuilder = new StringBuilder(24576);
		while (true) {
			try {
				objLfileLineStringBuilder.append(Strings.doConcat(	Strings.getRightTrimmedString(objPbufferedReader.readLine()),
																	Strings.strS_LINE_SEPARATOR));
			} catch (final Throwable objPthrowable) {
				break;
			}
		}
		try {
			objPbufferedReader.close();
		} catch (final Throwable objPthrowable) {
			Tools.err("Error while opening pattern header file");
		}
		objLfileLineStringBuilder.trimToSize();
		return objLfileLineStringBuilder.toString();
	}

