	final private String getLanguageString(Boolean bolPvaluesBoolean) {

		// Init section array lists :
		final ArrayList<String>[] strLsectionPropertyALA = new ArrayList[Language.bytS_SECTIONS_NUMBER];
		for (byte bytLsectionIndex = 0; bytLsectionIndex < Language.bytS_SECTIONS_NUMBER; ++bytLsectionIndex) {
			strLsectionPropertyALA[bytLsectionIndex] = new ArrayList<String>(24);
		}

		// Fill section array lists :
		for (int intLpropertyIndex = 0; intLpropertyIndex < Language.intS_LANGUAGE_PROPERTIES_NUMBER; ++intLpropertyIndex) {
			if (intLpropertyIndex != Language.intS_LANGUAGE_ISO_639_1_CODE && intLpropertyIndex != Language.intS_MESSAGE_PATTERNS_JM_FILE_HEADER
				&& intLpropertyIndex != Language.intS_MESSAGE_PATTERNS_JMP_FILE_HEADER
				&& intLpropertyIndex != Language.intS_MESSAGE_PATTERNS_JAP_FILE_HEADER) {
				final byte bytLsection = Language.bytS_PROPERTY_SECTION_A[intLpropertyIndex];
				final String strL =
									Strings.doConcat(	Strings.getFixedLengthString(	Language.strS_PROPERTY_NAME_A[intLpropertyIndex],
																						Language.intS_LANGUAGE_SECTION_PROPERTY_NAME_MAX_LENGTH_A[bytLsection]),
														" = ",
														bolPvaluesBoolean == Boolean.TRUE
																							? this.getPropertyValueString(intLpropertyIndex)
																							: bolPvaluesBoolean == null
																														? Language.getTokenedPropertyName(intLpropertyIndex)
																														: null,
														Strings.strS_LINE_SEPARATOR);
				strLsectionPropertyALA[bytLsection].add(strL);
			}
		}

		// Build file string :
		final StringBuilder objLfileStringBuilder =
													new StringBuilder((Language.intS_LANGUAGE_PROPERTIES_NAMES_MAX_LENGTH + 3 + 16 + 1)
																		* Language.intS_LANGUAGE_PROPERTIES_NUMBER);

		int intLsectionPropertiesMaxNumber = 0;
		for (final ArrayList<String> strLAL : strLsectionPropertyALA) {
			intLsectionPropertiesMaxNumber = Math.max(intLsectionPropertiesMaxNumber, strLAL.size());
		}
		final String[][] strLsectionPropertyAA = new String[Language.bytS_SECTIONS_NUMBER][intLsectionPropertiesMaxNumber];
		for (int intLsectionIndex = 0; intLsectionIndex < Language.bytS_SECTIONS_NUMBER; ++intLsectionIndex) {
			strLsectionPropertyALA[intLsectionIndex].toArray(strLsectionPropertyAA[intLsectionIndex]);
			for (int intLsectionPropertyIndex = 0; intLsectionPropertyIndex < intLsectionPropertiesMaxNumber; ++intLsectionPropertyIndex) {
				if (strLsectionPropertyAA[intLsectionIndex][intLsectionPropertyIndex] == null) {
					strLsectionPropertyAA[intLsectionIndex][intLsectionPropertyIndex] = Strings.strS_EMPTY;
				}
			}
			Arrays.sort(strLsectionPropertyAA[intLsectionIndex]);
			if (intLsectionIndex != Language.bytS_SECTION_ROOT) {
				objLfileStringBuilder.append(Strings.doConcat(	'[',
																Language.strS_SECTION_A[intLsectionIndex],
																']',
																Strings.strS_LINE_SEPARATOR,
																Strings.getCharsString(';', Language.strS_SECTION_A[intLsectionIndex].length() + 2),
																Strings.strS_LINE_SEPARATOR));
			}
			objLfileStringBuilder.append(Strings.strS_LINE_SEPARATOR);
			for (int intLpropertyIndex = 0; intLpropertyIndex < Language.intS_LANGUAGE_PROPERTIES_NUMBER; ++intLpropertyIndex) {
				if (intLpropertyIndex != Language.intS_LANGUAGE_ISO_639_1_CODE) {
					objLfileStringBuilder.append(strLsectionPropertyAA[intLsectionIndex][intLpropertyIndex]);
				}
			}
			objLfileStringBuilder.append(Strings.doConcat(	Strings.strS_LINE_SEPARATOR,
															this.strGpropertyA[Language.intS_MESSAGE_SEPARATOR_LINE],
															Strings.strS_LINE_SEPARATOR,
															Strings.strS_LINE_SEPARATOR));
		}
		objLfileStringBuilder.trimToSize();
		return objLfileStringBuilder.toString();
	}

