	final public String getPropertyValueString(int intPpropertyIndex, String strPtokenValue) {
		String strL =
						(intPpropertyIndex >= 0 && intPpropertyIndex < Language.intS_LANGUAGE_PROPERTIES_NUMBER
																												? this.strGpropertyA[intPpropertyIndex]
																												: null);
		if (strPtokenValue != null && strL != null) {
			strL = Language.objS_TOKEN_PATTERN.matcher(strL).replaceAll(Matcher.quoteReplacement(strPtokenValue));
		}
		return strL;
	}

	/**
	 * Method description
	 * 
	 * @see
	 * @param strP
	 * @param bytPpatternsManagerType
	 * @return
	 */
	final public String getTranslatedTokensString(String strP, byte bytPpatternsManagerType) {
		boolean bolLskip = true;
		String strL = new String(strP);
		for (byte bytLtokenIndex = 0; bytLtokenIndex < Language.bytS_TOKENS_NUMBER; ++bytLtokenIndex) {
			if (bytLtokenIndex != Language.bytS_TOKEN_FILE_NAME) {
				// Set if a replace has to be done :
				switch (bytPpatternsManagerType) {
					case Constants.bytS_MANAGER_NO_PATTERN:
						bolLskip =
									bytLtokenIndex != Language.bytS_TOKEN_JUGGLE_MASTER_PRO
										&& bytLtokenIndex != Language.bytS_TOKEN_NO_PATTERN_IN_THIS_FILE
										&& bytLtokenIndex != Language.bytS_TOKEN_CHOOSE_YOUR_PATTERNS_OR_LOOK_FOR_HELP
										&& bytLtokenIndex != Language.bytS_TOKEN_LOOK_FOR_HELP;
						break;
					case Constants.bytS_MANAGER_NEW_PATTERN:
						// bolLskip =
						// bytLtokenIndex != Language.bytS_TOKEN_JUGGLE_MASTER_PRO && bytLtokenIndex != Language.bytS_TOKEN_NEW_PATTERN_MENU
						// && bytLtokenIndex != Language.bytS_TOKEN_CHOOSE_YOUR_PATTERNS_OR_LOOK_FOR_HELP;
						strL =
								MenusTools.getNoMnemonicLabel(strL.replace(	Language.strS_TOKEN_A[bytLtokenIndex],
																			this.getPropertyValueString(Language.intS_LANGUAGE_TOKEN_INDEX_A[bytLtokenIndex])));
						break;

					case Constants.bytS_MANAGER_NEW_ABSTRACT_LANGUAGE:
						bolLskip = true;
						break;

					case Constants.bytS_MANAGER_JM_PATTERN:
						bolLskip =
									bytLtokenIndex != Language.bytS_TOKEN_JUGGLE_MASTER_PRO
										&& bytLtokenIndex != Language.bytS_TOKEN_CHOOSE_YOUR_PATTERNS_OR_LOOK_FOR_HELP;
						break;
					case Constants.bytS_MANAGER_FILES_PATTERNS:
						bolLskip =
									bytLtokenIndex != Language.bytS_TOKEN_PATTERNS_IMPORTED_FROM
										&& bytLtokenIndex != Language.bytS_TOKEN_SITESWAPS_IMPORTED_FROM
										&& bytLtokenIndex != Language.bytS_TOKEN_JUGGLE_MASTER_PRO;
						if (bytLtokenIndex == Language.bytS_TOKEN_JUGGLE_MASTER_PRO) {
							strL =
									strL.replace(	Language.strS_TOKEN_A[bytLtokenIndex],
													this.getPropertyValueString(Language.intS_LANGUAGE_TOKEN_INDEX_A[bytLtokenIndex]));
						}
						break;
				}
				if (bolLskip) {
					continue;
				}

				// Replace token :
				strL =
						strL.replace(	Language.strS_TOKEN_A[bytLtokenIndex],
										this.getPropertyValueString(Language.intS_LANGUAGE_TOKEN_INDEX_A[bytLtokenIndex]));
			}
		}
		return strL;
	}

